import React, { useState } from "react";
import AddIcon from '@material-ui/icons/Add';
import Fab from '@material-ui/core/Fab';
import Zoom from "@material-ui/core/Zoom"

function CreateArea(props) {
    const [zoom,setZoom] = useState(false);
    const [note,setNote] = useState({
        title:"",
        content:""
    });

    function handleChange(event){
        const {name,value} = event.target
        setNote(prevNote => {
            return {
                ...prevNote,
                [name] : value
            }
        });
    }

    function handleClick(){
      setZoom(true);
    }

    function submitAdd(event){
        props.handleAdd(note);
        event.preventDefault();
        setNote({
            title:"",
            content:""
        })
    }

  return (
    <div>
      {zoom ? <form className="create-note">
        {<input onChange={handleChange} name="title" placeholder="Title" value={note.title} />}
        <textarea onClick={handleClick} onChange={handleChange} name="content" placeholder="Take a note..." rows="3" value={note.content} />
        <Zoom in={true}>
        <Fab onClick={submitAdd} >
          <AddIcon />
        </Fab>
        </Zoom>
      </form> : <form> <textarea onClick={handleClick} onChange={handleChange} name="content" placeholder="Take a note..." rows="3" value={note.content} /></form>}
    </div>
  );
}

export default CreateArea;
